import {Component, Input} from '@angular/core';
import {ItemPair} from "../../../api/models/item-pair";
import {ItemsService} from "../../../api/services/items.service";
import {ItemRelation} from "../../../api/models";

@Component({
  selector: 'app-pair',
  templateUrl: './pair.component.html',
  styleUrls: ['./pair.component.scss']
})
export class PairComponent {

  @Input() pair!: ItemPair;

  constructor(private itemsService:ItemsService) {
  }

  addRelation(from:number, to:number){
    this.itemsService.itemsControllerAddRelation({body:{
        from: from,
        to: to
      }})


  }

}
<span [ngClass]="{'bold-pair':pair.item1.id === pair.relation?.from}">{{pair.item1.name}}</span>
. <span [ngClass]="{'bold-pair':!pair.relation}">=</span> .
<span [ngClass]="{'bold-pair':pair.item2.id === pair.relation?.from}">{{pair.item2.name}}</span>
