import { Component } from '@angular/core';

@Component({
  selector: 'app-add-item',
  templateUrl: './add-item.component.html',
  styleUrls: ['./add-item.component.scss']
})
export class AddItemComponent {

}
<form [formGroup]="form">
  <small>Name</small>
  <input formControlName="itemName" id="itemName" placeholder="newItem">
  <button (click)="addNewItem($event)" [disabled]="form.invalid">Add</button>
</form>
