import { Component } from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-add-item',
  templateUrl: './add-item.component.html',
  styleUrls: ['./add-item.component.scss']
})
export class AddItemComponent {

  readonly form: FormGroup = this.formBuilder.group({
    itemName: [null, Validators.required]
  });

  constructor(private formBuilder: FormBuilder) {
  }

  addNewItem(event: any): void {
    updateFormValidity(this.form);
    if (!this.form.valid) {
      event.target.blur();
      return;
    }
    const projectDocumentCreate: ProjectDocumentCreate = this.getFormValue();

    this.projectService.addProjectDocument({id: this.projectId, body: projectDocumentCreate}).subscribe()
  }


}
