import {Component, Input} from '@angular/core';
import {ItemPair} from "../../../api/models/item-pair";
import {ItemsService} from "../../../api/services/items.service";

@Component({
  selector: 'app-pair',
  templateUrl: './pair.component.html',
  styleUrls: ['./pair.component.scss']
})
export class PairComponent {

  @Input() pair!: ItemPair;

  constructor(private itemsService: ItemsService) {
  }

  addRelation(from: number, to: number) {
    this.itemsService.itemsControllerAddRelation({
      body: {
        from: from,
        to: to
      }
    })
  }

  deleteRelation(){
    this.itemsService.itemsControllerDeleteRelation({
      body: {
        from: from,
        to: to
      }
    })

  }

}
<span *ngIf="pair.item1.id === pair.relation?.from" class='bold-pair'>{{pair.item1.name}}</span>
<a (click)="addRelation(pair.item1.id,pair.item2.id)" *ngIf="pair.item1.id !== pair.relation?.from"
   href="#">{{pair.item1.name}}</a>
<span *ngIf="!pair.relation" class='bold-pair'> . = . </span>
<a *ngIf="!!pair.relation"> . = . </a>
<span *ngIf="pair.item2.id === pair.relation?.from" class='bold-pair'>{{pair.item2.name}}</span>
<a (click)="addRelation(pair.item2.id,pair.item1.id)" *ngIf="pair.item2.id !== pair.relation?.from"
   href="#">{{pair.item2.name}}</a>
